package com.example.helloworld;

/**
 * Provides a better reference to the resources available and/or defined for the application.
 *
 * <p>
 * The "R" class that is automatically generated is terribly named, because it could be "Resources",
 * but it could also be "Railroad", "Risky", "RidiculouslyNamedClassThatCannotBeNamedProperly", etc.
 * </p>
 */
public class Resources {

    public static int mainActivityLayoutId() {
        return R.layout.activity_main;
    }

    public static int displayMessageActivityLayoutId() {
        return R.layout.activity_display_message;
    }

    public static int editTextViewId() {
        return R.id.editText;
    }

    public static int textViewViewId() {
        return R.id.textView;
    }

}
